var room = 1;

function education_fields() {

    room++;
    var objTo = document.getElementById('education_fields')
    var divtest = document.createElement("div");
    divtest.setAttribute("class", "form-group removeclass" + room);
    var rdiv = 'removeclass' + room;
    divtest.innerHTML = '<form class="floating-labels"><div class="row"><div class="col-lg-6"><div class="form-group"><select class="select2 form-control custom-select" style="width: 100%; height:36px;"><option>Select</option><optgroup label="Alaskan/Hawaiian Time Zone"><option value="AK">Alaska</option><option value="HI">Hawaii</option></optgroup><optgroup label="Pacific Time Zone"><option value="CA">California</option><option value="NV">Nevada</option></optgroup><optgroup label="Mountain Time Zone"><option value="AZ">Arizona</option><option value="CO">Colorado</option></optgroup> </select></div></div><div class="col-lg-4 switchery-demo"><input type="checkbox" checked class="js-switch" data-color="#7460ee" /><input type="checkbox" checked class="js-switch" data-color="#3d3b3b" /></div></div></div></form><button class="btn btn-danger" type="button" onclick="remove_education_fields(' + room + ');"> <i class="fa fa-minus"></i> </button></div></div></div></div><div class="clear"></div></row>';

    objTo.appendChild(divtest)
}

function remove_education_fields(rid) {
    $('.removeclass' + rid).remove();
}